import { useEffect, useState } from "react";
import UserAPI from "../API/UserAPI";
import Image from "../Share/img/Image";

function Infor() {
  const userId = localStorage.getItem("id_user");
  const expiryDate = localStorage.getItem("expiryDate");
  const timeRemaining = new Date(expiryDate).getTime() - new Date().getTime();

  const [username, setUsername] = useState();
  const [email, setEmail] = useState();
  const [phone, setPhoent] = useState();

  useEffect(() => {
    if (!expiryDate) {
      return window.location.replace("/signin");
    }

    // auto logout when expirydate token
    const autoLogout = (miliseconds) => {
      setTimeout(() => {
        localStorage.clear();
        window.location.replace("/signin");
      }, miliseconds);
    };

    autoLogout(timeRemaining);
  }, [timeRemaining]);

  useEffect(() => {
    const getUser = async () => {
      const response = await UserAPI.getUser(userId);
      console.log(response);
    };
    getUser();
  }, []);
  return (
    <div className="container">
      <div className="row">
        <section
          className="hero pb-3 bg-cover bg-center d-flex align-items-center"
          style={{ backgroundImage: `url(${Image.banner})` }}
        >
          <div className="container py-5">
            <div className="row px-4 px-lg-5">
              <div className="col-lg-6">
                <p className="text-muted small text-uppercase mb-2">
                  New Inspiration 2023
                </p>
                <h1 className="h2 text-uppercase mb-3">
                  20% off on new season
                </h1>
                <a className="btn btn-dark" href="./shop">
                  Browse collections
                </a>
              </div>
            </div>
          </div>
        </section>
        <form>
          <div>
            <label htmlFor="">Username</label>
            <input />
          </div>
          <div>
            <label htmlFor="">Email</label>
            <input />
          </div>
          <div>
            <label htmlFor="">Phone Number </label>
            <input />
          </div>
        </form>
      </div>
    </div>
  );
}
export default Infor;
