const initialState = {
  idUser: "",
};

const ReducerSession = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_SESSION":
      const stateLogin = [...state.idUser];
      stateLogin.idUser = action.data;
      return stateLogin;

    case "DELETE_SESSION":
      const stateLogout = [...state.idUser];
      stateLogout.idUser = action.data;
      localStorage.clear();
      return stateLogout;

    default:
      return state;
  }
};

export default ReducerSession;
