import axiosClient from "./axiosClient";
const token = localStorage.getItem("token");
const CheckoutAPI = {
  postEmail: (query) => {
    const url = `/email${query}`;
    return axiosClient.post(
      url,
      {},
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
  },
  postOrder: (body) => {
    const url = `/orders`;
    console.log(url);
    return axiosClient.post(
      url,
      { body },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
  },
};

export default CheckoutAPI;
