import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import HistoryAPI from "../../API/HistoryAPI";
import Image from "../../Share/img/Image";
function DetailHistory(props) {
  const { id } = useParams();

  const [cart, setCart] = useState([]);

  const [information, setInformation] = useState({});

  const [openPay, setOpenPay] = useState(false);
  const [payMethod, setPayMethod] = useState();

  useEffect(() => {
    const fetchData = async () => {
      const response = await HistoryAPI.getDetail(id);

      setCart(response.products);
      console.log(response);

      setInformation(response);
    };

    fetchData();
  }, []);

  return (
    <div className="container">
      <section
        className="hero pb-3 bg-cover bg-center d-flex align-items-center"
        style={{ backgroundImage: `url(${Image.banner})` }}
      >
        <div className="container py-5">
          <div className="row px-4 px-lg-5">
            <div className="col-lg-6">
              <h1 className="h2 text-uppercase mb-0">Detail History</h1>
            </div>
          </div>
        </div>
      </section>

      <div className="p-5">
        <h1 className="h2 text-uppercase">Information Order</h1>
        <p>ID User: {information.userId}</p>
        <p>Full Name: {information.fullname}</p>
        <p>Phone: {information.phone}</p>
        <p>Address: {information.address}</p>
        <p>Total: {information.total}$</p>
        <button
          onClick={() => {
            setOpenPay(!openPay);
          }}
          className="btn btn-dark mt-4"
        >
          Pay
        </button>
      </div>
      {openPay && (
        <div>
          <form>
            <div className="container">
              <div className="row">
                <div className="col-lg-3 form-group ml-5">
                  <select
                    onChange={(e) => {
                      setPayMethod(e.target.value);
                    }}
                    className="form-select form-select-lg p-2 "
                  >
                    <option>Select Payment Method</option>
                    <option>Credit Card</option>
                    <option>Cash</option>
                  </select>
                </div>
                {payMethod === "Credit Card" && (
                  <div className="col-lg-4 form-group">
                    <label
                      className="text-small text-uppercase"
                      htmlFor="Email"
                    >
                      Card Number
                    </label>
                    <input
                      className="form-control form-control-lg"
                      type="text"
                      placeholder="Enter Your Card Number Here!"
                    />
                  </div>
                )}
                {payMethod !== "Select Payment Method" && (
                  <div>
                    <button className="btn btn-dark mt-4">Confirm </button>
                  </div>
                )}
              </div>
            </div>
          </form>
        </div>
      )}

      <div className="table-responsive pt-5 pb-5">
        <table className="table">
          <thead className="bg-light">
            <tr className="text-center">
              <th className="border-0" scope="col">
                {" "}
                <strong className="text-small text-uppercase">
                  ID Product
                </strong>
              </th>
              <th className="border-0" scope="col">
                {" "}
                <strong className="text-small text-uppercase">Image</strong>
              </th>
              <th className="border-0" scope="col">
                {" "}
                <strong className="text-small text-uppercase">Name</strong>
              </th>
              <th className="border-0" scope="col">
                {" "}
                <strong className="text-small text-uppercase">Price</strong>
              </th>
              <th className="border-0" scope="col">
                {" "}
                <strong className="text-small text-uppercase">Count</strong>
              </th>
            </tr>
          </thead>
          <tbody>
            {cart &&
              cart.map((value) => (
                <tr className="text-center" key={value._id}>
                  <td className="align-middle border-0">
                    <h6 className="mb-0">{value.productId._id}</h6>
                  </td>
                  <td className="pl-0 border-0">
                    <div className="media align-items-center justify-content-center">
                      <Link
                        className="reset-anchor d-block animsition-link"
                        to={`/detail/${value.productId._id}`}
                      >
                        <img src={value.productId.img1} alt="..." width="200" />
                      </Link>
                    </div>
                  </td>
                  <td className="align-middle border-0">
                    <h6 className="mb-0">{value.productId.name}</h6>
                  </td>
                  <td className="align-middle border-0">
                    <h6 className="mb-0">{value.productId.price}</h6>
                  </td>
                  <td className="align-middle border-0">
                    <h6 className="mb-0">{value.quantity}</h6>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DetailHistory;
